import React from "react";
import { IoMdTrophy } from "react-icons/io";
import { FaHandshake } from "react-icons/fa";
import { CgWebsite } from "react-icons/cg";
import styled from "styled-components";
import Card from "./Card";
import { Slide } from "react-awesome-reveal";

const Services = () => {
  return (
    <Container id="values">
      <Slide direction="down">
        <h4>
          My <span className="green">values</span>
        </h4>
        <h1>What I Believe In</h1>
      </Slide>
      <Cards>
        <Slide direction="left">
          <Card
            Icon={IoMdTrophy}
            title={"Perseverance"}
            disc={`"Attitude is a little thing that makes a big difference." - Winston Churchill. I believe that with the right attitude, I can overcome any obstacle. I believe that with the right attitude, I can learn anything. I believe that with the right attitude, I can achieve anything.`}
          />
        </Slide>
        <Slide direction="up">
          <Card
            Icon={FaHandshake}
            title={"Respect"}
            disc={`The cornerstone of my values is respect. With respect, I can build trust and rapport with my teammates and clients. With respect, I will listen to and understand the needs of my teammates and clients. With respect, I will be able to communicate my ideas and opinions in a way that is constructive and helpful.`}
          />
        </Slide>
        <Slide direction="right">
          <Card
            Icon={CgWebsite}
            title={"web designer"}
            disc={`Lorem ipsum dolor sit amet consectetur 
                adipisicing elit. Commodi et asperiores cum exercitationem officia rem amet minus magnam? Cum, voluptatem?`}
          />
        </Slide>
      </Cards>
    </Container>
  );
};

export default Services;

const Container = styled.div`
  width: 80%;
  max-width: 1280px;
  margin: 0 auto;
  padding: 3rem 0;
  @media (max-width: 840px) {
    width: 90%;
  }

  h1 {
    padding-top: 1rem;
  }
`;
const Cards = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  margin-top: 4rem;
  gap: 1rem;
`;
